import React from "react";
import { ReactComponent as Logo } from "./figure.svg";

export default function Website() {
  const sideBySideContainer = {
    display: "flex",
  };

  const styleTitle1 = {
    fontSize: 72,
    paddingTop: "100px",
    // paddingRight: "700px",
    bold: { fontWeight: "bold" },
  };
  const styleTitle2 = {
    fontSize: 72,
    paddingTop: "0px",
    // paddingRight: "700px",
    bold: { fontWeight: "bold" },
    italic: { fontStyle: "italic" },
    underline: { textDecorationLine: "underline" },
  };
  const styleSubtitle = {
    fontSize: 36,
    textAlign: "right",
    paddingRight: "700px",
  };

  const centering = {
    textAlign: "center",
  };

  const right = {
    textAlign: "right",
    paddingTop: "100px",
    paddingRight: "50px",
  };

  const styleStats = {
    fontSize: 24,
  };

  return (
    <div>
      <div style={sideBySideContainer}>
        <div style={right}>
          <h1 style={styleTitle1}>
            <strong>Lecture Videos...</strong>
          </h1>
          <h1 style={styleTitle2}>
            <strong>Summarized.</strong>
          </h1>
        </div>
        <Logo />
      </div>
      <h3 style={styleSubtitle}>Can’t understand your lectures at ×2 speed?</h3>
      <h3 style={styleSubtitle}>
        Get a shorter and readable transcript instead!
      </h3>
      <button className="btn m-4 btn-primary btn-lg">Upload</button>
      <p style={styleStats}>X minutes of videos uploaded!</p>
      <p style={styleStats}>X minutes of words removed!</p>
      <p style={styleStats}>X% summary satisfaction rate!</p>
    </div>
  );
}
